import React, { Component, useState } from "react"
export class App extends Component {

  state = {
    value: "an"
  }
  changeValue(e){
    this.setState({value : e.target.value})

  }
  render() {
  return(
    <>
    <h1>App component</h1>
    <input value = {this.state.value} placeholder="Enter uou"  onChange={ (e) => {  this.  changeValue(e) } } />
    {JSON.stringify(this.state)}
    {this.state.value.length == 10 ?<p> valid number </p> :<p>Invalid number</p>}
      </> 
  )
  }
}


export default App
