import React from "react";
import Home from "./Home"
class App extends React.Component {


  state = {
    count: 0

  }
  incrementCount = () => {
    this.setState((prevState) => {
      return { count: prevState.count + 3}
    })


  }
  DecrementCount = () => {
    this.setState((prevstate) => {
      return { count: prevstate.count - 3 }
    })

  }

  render() {

    return (
      <>
        <p> App content</p>
        <p>  <button onClick={this.incrementCount} > + </button> {this.state.count}<button onClick={this.DecrementCount}>-</button> </p>
      </>
    )
  }
}

export default App;
