import React, { Component, useState } from "react"
export class App extends Component {

  state = {
    value: ""
  }
  changeValue(e){
    this.setState({value : e.target.value})

  }
  render() {
  return(
    <>
    <h1>App component</h1>
    <input value = {this.state.value}   onChange={ (e) => {  changeValue(e) } } />
    {JSON.stringify(this.state)}
      </> 
  )
  }
}


export default App
